import "bootstrap/dist/css/bootstrap.min.css";

import { createApp } from "vue";
import { createRouter, createWebHistory } from "vue-router";

import App from "./App.vue";

// UI ELEMENTS
import NavBar from "./components/ui/NavBar";
import SideBar from "./components/ui/TheSidebar";
import TheFooter from "./components/ui/TheFooter";

// PAGES - ACCOUNT
import UserHome from "./components/pages/UserHome";
import AccountSettings from "./components/pages/account/AccountSettings";
import AccountOrders from "./components/pages/account/AccountOrders";
import AccountInvoices from "./components/pages/account/AccountInvoices";

//PAGES - GIFTS
import GiftTracking from "./components/pages/gift/GiftTracking";
//

// ROUTER
const router = createRouter({
  history: createWebHistory(),
  routes: [
    {
      path: "/",
      component: UserHome,
    },
    { path: "/account-settings", component: AccountSettings },
    { path: "/account-orders", component: AccountOrders },
    { path: "/account-invoices", component: AccountInvoices },
    { path: "/gift-tracking", component: GiftTracking },
  ],
});

const app = createApp(App);

app.use(router);
app.use(BootstrapIconsPlugin);

// COMPONENTS
app.component("nav-bar", NavBar);
app.component("side-bar", SideBar);
app.component("the-footer", TheFooter);

app.mount("#app");

//CSS
import "./assets/css/docs.min.css";
import "./assets/css/snippets.min.css";
import "./assets/css/theme.min.css";
import { BootstrapIconsPlugin } from "bootstrap-icons-vue";
// import "bootstrap-vue/dist/bootstrap-vue-icons.min.css";

//JS
import "bootstrap/dist/js/bootstrap.js";
// import "./assets/js/hs.theme-appearance-charts.js";
// import "./assets/js/hs.theme-appearance.js";
