import { createStore } from "vuex";

import rootMutations from "./mutations";
import rootGetters from "./getters";
import rootActions from "./actions";

const store = createStore({
  state() {
    return {
      isLoggedIn: false,
      firstName: "",
      lastName: "",
      email: "",
      password: "",
      passwordConfirm: "",
    };
  },
  actions: rootActions,
  mutations: rootMutations,
  getters: rootGetters,
});

export default store;
